  constructor(
    private _elementRef: ElementRef,
    private _renderer: Renderer2) { 

      function style(property: string, value: string){
        _renderer.setStyle(_elementRef.nativeElement, property, value);
      }
  }




  call = this.style('background-color', 'yellow');
}
