   selector: 'my-first-component',
   template: `<span>, {{ name }}!</span>`
})

export class MyFirstComponent implements OnInit{
   constructor(){}

   @Input('thename') name: string = '';
   @Input('othername') name2 : string = '';

   ngOnInit(){}
}
